function validalogin(form) {
var login=document.formulario.login.value;
var senha=document.formulario.senha.value;

if (login=="") {
window.alert("O campo login deve ser preenchido!");
document.formulario.login.focus();
return false;
}

if (senha=="") {
window.alert("O campo senha deve ser preenchido!");
document.formulario.senha.focus();
return false;
}

return true;
document.formulario.submit();
}
</script>
